import React, {setState} from 'react';
import './style.css';

function showCards() {
  setState({show: false})
}

const Button = ({data, show}) => {

  console.log(show)
  return <>
  <a className={"btn"} onClick={showCards}>{data.buttonLabelShow}</a>
  </>
}

export default Button;
